    end
  end
  pal(p)
  spr(id, x, y, ck, 1, 0)
  spr(id, x + width - 8, y, ck, 1, 1)
  pal()
  if label then
    prints(label.text, x + label.x, y + label.y, label.bg, label.fg, label.shadow)
