  spr(ROCKET_SILO_ID, sx + 16, sy, 1, 1, 1, 0, 2, 2)
  
  if self.state == 'raise' or self.state == 'launch' then
    clip(sx + 8, (sy + 24) - 72, 16, 72)
    spr(ROCKET_ID, sx + 8, sy + 16 - self.anim_frame, 1, 1, 0, 0, 2, 6)
